import React, { useState, useRef, useEffect } from 'react';
import io from 'socket.io-client';
import { useSpeechRecognitionHook } from '../hooks/useSpeechRecognition';
import { speechSynthesizer } from '../utils/speechSynthesis';
import './ChatInterface.css';

const ChatInterface = ({ user }) => {
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const [connectionStatus, setConnectionStatus] = useState('disconnected');
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [currentlySpeakingId, setCurrentlySpeakingId] = useState(null);
  const [showLanguageMenu, setShowLanguageMenu] = useState(false);
  const [isVideoLoaded, setIsVideoLoaded] = useState(false);
  const messagesEndRef = useRef(null);
  const socketRef = useRef();
  const videoRef = useRef(null);

  const {
    transcript,
    isListening,
    startListening,
    stopListening,
    supportedLanguages,
    selectedLanguage,
    setSelectedLanguage,
    browserSupportsSpeechRecognition
  } = useSpeechRecognitionHook(handleSpeechResult);

  // Enhanced text cleaning function for speech
  const cleanTextForSpeech = (text) => {
    if (typeof text !== 'string') return String(text);
    
    console.log('Original text for speech:', text);
    
    // Remove all formatting but preserve the actual content
    let cleanedText = text
      // Remove markdown formatting but keep text
      .replace(/\*\*(.*?)\*\*/g, '$1')
      .replace(/\*(.*?)\*/g, '$1')
      .replace(/`(.*?)`/g, '$1')
      .replace(/#/g, '')
      .replace(/\*/g, '')
      .replace(/~/g, '')
      .replace(/_/g, ' ')
      
      // Remove bullet points and numbering, replace with pauses
      .replace(/\n\s*[-*â€¢]\s*/g, '. ')
      .replace(/\n\s*\d+\.\s*/g, '. ')
      
      // Clean up punctuation for natural speech
      .replace(/\s*\.\s*\.\s*\./g, '. ')
      .replace(/\s*,\s*/g, ' ')
      .replace(/\s*;\s*/g, '. ')
      .replace(/\s*:\s*/g, ' ')
      .replace(/\s*!\s*/g, '. ')
      .replace(/\s*\?\s*/g, '. ')
      
      // Remove other special characters
      .replace(/[\[\](){}]/g, ' ')
      .replace(/["']/g, ' ')
      .replace(/[@#$%&+=<>\/\\\|]/g, ' ')
      
      // Handle multiple newlines and spaces
      .replace(/\n+/g, '. ')
      .replace(/\s+/g, ' ')
      .trim();

    // Add natural pauses for better speech flow
    cleanedText = cleanedText
      .replace(/\.\s+/g, '. ')
      .replace(/\s{2,}/g, ' ');

    // Ensure the text ends with a period if it doesn't have proper punctuation
    if (cleanedText.length > 0 && !/[.!?]$/.test(cleanedText)) {
      cleanedText += '.';
    }

    console.log('Cleaned text for speech:', cleanedText);
    return cleanedText;
  };

  // Initialize speech synthesis and video background
  useEffect(() => {
    const initializeApp = async () => {
      try {
        // Initialize speech synthesis
        await speechSynthesizer.init();
        console.log('Speech synthesis initialized successfully');
        
        // Setup video background
        if (videoRef.current) {
          videoRef.current.playbackRate = 0.8;
          videoRef.current.loop = true;
          videoRef.current.muted = true;
          
          videoRef.current.addEventListener('loadeddata', () => {
            setIsVideoLoaded(true);
          });
          
          try {
            await videoRef.current.play();
          } catch (error) {
            console.log('Video autoplay prevented:', error);
          }
        }
      } catch (error) {
        console.error('Initialization error:', error);
      }
    };

    initializeApp();

    return () => {
      stopSpeaking();
      stopListening();
    };
  }, []);

  function handleSpeechResult(text, language) {
    if (text.trim().length > 0) {
      setInputMessage(text);
      // Auto-send the speech result after a short delay
      setTimeout(() => {
        handleSendMessage(null, text);
      }, 500);
    }
  }

  const handleMicClick = () => {
    if (isListening) {
      stopListening();
    } else {
      startListening(selectedLanguage);
    }
  };

  const handleLanguageSelect = (languageCode) => {
    setSelectedLanguage(languageCode);
    setShowLanguageMenu(false);
    if (isListening) {
      stopListening();
      setTimeout(() => {
        startListening(languageCode);
      }, 300);
    }
  };

  const speakMessage = async (text, messageId = null) => {
    if (!text || isSpeaking) {
      console.log('Cannot speak: no text or already speaking');
      return;
    }
    
    console.log('Attempting to speak message in language:', selectedLanguage);
    console.log('Message content:', text.substring(0, 100) + '...');

    // Check if speech synthesis is supported
    if (!speechSynthesizer.isSupported()) {
      console.error('Speech synthesis not supported');
      alert('Text-to-speech is not supported in your browser. Please try Chrome or Edge.');
      return;
    }

    try {
      setCurrentlySpeakingId(messageId);
      setIsSpeaking(true);
      
      // Ensure speech synthesis is initialized
      if (!speechSynthesizer.isInitialized) {
        await speechSynthesizer.init();
      }
      
      // Clean the text before speaking
      const cleanedText = cleanTextForSpeech(text);
      
      if (!cleanedText || cleanedText.trim().length === 0) {
        console.log('No text to speak after cleaning');
        return;
      }
      
      await speechSynthesizer.speak(cleanedText, selectedLanguage);
      console.log('Speech completed successfully');
      
    } catch (error) {
      console.error('Speech synthesis error:', error);
      
      // Fallback: Try with English if the selected language fails
      if (selectedLanguage !== 'en-US') {
        console.log('Trying fallback to English...');
        try {
          const cleanedText = cleanTextForSpeech(text);
          await speechSynthesizer.speak(cleanedText, 'en-US');
        } catch (fallbackError) {
          console.error('Fallback speech also failed:', fallbackError);
          alert('Unable to read the message aloud. Your browser may not support speech synthesis for this language.');
        }
      } else {
        alert('Unable to read the message aloud. Please check your browser settings.');
      }
    } finally {
      setIsSpeaking(false);
      setCurrentlySpeakingId(null);
    }
  };

  const stopSpeaking = () => {
    console.log('Stopping speech synthesis');
    speechSynthesizer.stop();
    setIsSpeaking(false);
    setCurrentlySpeakingId(null);
  };

  const renderSafeContent = (content) => {
    if (typeof content === 'string') return content;
    if (typeof content === 'number') return content.toString();
    if (typeof content === 'object' && content !== null) {
      try {
        return JSON.stringify(content);
      } catch (e) {
        return String(content);
      }
    }
    return String(content);
  };

  const formatMessageWithLineBreaks = (text) => {
    if (typeof text !== 'string') return text;
    
    const lines = text.split('\n');
    return lines.map((line, index) => {
      if (line.trim() === '') {
        return <br key={index} />;
      }
      
      const parts = line.split(/\*\*(.*?)\*\*/g);
      const formattedLine = parts.map((part, i) => {
        if (i % 2 === 1) {
          return <strong key={i}>{part}</strong>;
        }
        return part;
      });
      
      return (
        <React.Fragment key={index}>
          {formattedLine}
          {index < lines.length - 1 && <br />}
        </React.Fragment>
      );
    });
  };

  useEffect(() => {
    // Initialize socket connection
    socketRef.current = io('http://localhost:5000', {
      auth: {
        token: localStorage.getItem('token')
      }
    });

    socketRef.current.on('connect', () => {
      console.log('Connected to server');
      setConnectionStatus('connected');
    });

    socketRef.current.on('disconnect', () => {
      console.log('Disconnected from server');
      setConnectionStatus('disconnected');
    });

    socketRef.current.on('chat_message', (data) => {
      setIsTyping(false);
      const newMessage = {
        id: Date.now(),
        text: renderSafeContent(data.message),
        sender: 'bot',
        timestamp: new Date()
      };
      
      setMessages(prev => [...prev, newMessage]);
    });

    socketRef.current.on('bot_typing', () => {
      setIsTyping(true);
    });

    socketRef.current.on('error', (error) => {
      console.error('Socket error:', error);
      setIsTyping(false);
      setMessages(prev => [...prev, {
        id: Date.now(),
        text: "Sorry, I'm having trouble connecting. Please try again.",
        sender: 'bot',
        timestamp: new Date()
      }]);
    });

    // Fetch chat history
    fetchChatHistory();

    return () => {
      if (socketRef.current) {
        socketRef.current.disconnect();
      }
      stopSpeaking();
      stopListening();
    };
  }, []);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const fetchChatHistory = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/chat/history', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      
      if (response.ok) {
        const history = await response.json();
        const formattedHistory = history.map(chat => ({
          id: chat.chat_id || Date.now(),
          text: renderSafeContent(chat.query),
          sender: 'user',
          timestamp: new Date(chat.timestamp),
          response: renderSafeContent(chat.response)
        }));
        
        // Add both user queries and bot responses to messages
        const allMessages = [];
        formattedHistory.forEach(chat => {
          allMessages.push({
            id: chat.id + '_user',
            text: chat.text,
            sender: 'user',
            timestamp: chat.timestamp
          });
          allMessages.push({
            id: chat.id + '_bot',
            text: chat.response,
            sender: 'bot',
            timestamp: new Date(chat.timestamp.getTime() + 1000)
          });
        });
        
        setMessages(allMessages.sort((a, b) => a.timestamp - b.timestamp));
      }
    } catch (error) {
      console.error('Error fetching chat history:', error);
    }
  };

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  const handleSendMessage = (e, speechText = null) => {
    if (e) e.preventDefault();
    
    const messageText = speechText || inputMessage;
    if (messageText.trim() === '') return;

    const newMessage = {
      id: Date.now(),
      text: messageText,
      sender: 'user',
      timestamp: new Date()
    };

    setMessages(prev => [...prev, newMessage]);
    setInputMessage('');
    setIsTyping(true);

    if (socketRef.current) {
      socketRef.current.emit('user_message', {
        message: messageText,
        userId: user?.id,
        language: selectedLanguage
      });
    }
  };

  const getLanguageName = (code) => {
    const lang = supportedLanguages.find(l => l.code === code);
    return lang ? lang.nativeName : code;
  };

  return (
    <div className="chat-container">
      {/* Video Background */}
      <div className="video-background">
        <video
          ref={videoRef}
          className="background-video"
          autoPlay
          muted
          loop
          playsInline
          onLoadedData={() => setIsVideoLoaded(true)}
        >
          <source src="/videos/medical-background.mp4" type="video/mp4" />
          <source src="/videos/medical-background.webm" type="video/webm" />
          Your browser does not support the video tag.
        </video>
        {!isVideoLoaded && (
          <div className="video-placeholder">
            <div className="pulse-animation"></div>
          </div>
        )}
        <div className="video-overlay"></div>
      </div>

      {/* Header */}
      <header className="chat-header">
        <div className="header-left">
          <div className="logo">
            <div className="logo-icon">
              <div className="rotating-plus">+</div>
            </div>
            <h2>Health Assistant</h2>
          </div>
        </div>
        
        <div className="header-center">
          <div className="user-info">
            <span className="welcome-text">Welcome,</span>
            <span className="user-name">{user?.name || 'User'}</span>
          </div>
        </div>
        
        <div className="header-right">
          <div className="connection-status-wrapper">
            <div className={`status-dot ${connectionStatus}`}></div>
            <span className="status-text">
              {connectionStatus === 'connected' ? 'Connected' : 'Disconnected'}
            </span>
          </div>
          
          <div className="language-selector">
            <button 
              className="language-btn"
              onClick={() => setShowLanguageMenu(!showLanguageMenu)}
              title="Select Language"
              aria-expanded={showLanguageMenu}
            >
              <span className="language-icon">ðŸŒ</span>
              <span className="language-text">{getLanguageName(selectedLanguage)}</span>
              <span className="dropdown-arrow">â–¼</span>
            </button>
            
            {showLanguageMenu && (
              <div className="language-menu">
                <div className="language-menu-header">
                  <h4>Select Language</h4>
                  <button 
                    className="close-menu-btn"
                    onClick={() => setShowLanguageMenu(false)}
                    aria-label="Close language menu"
                  >
                    Ã—
                  </button>
                </div>
                <div className="language-options-list">
                  {supportedLanguages.map((language) => (
                    <button
                      key={language.code}
                      className={`language-option ${selectedLanguage === language.code ? 'selected' : ''}`}
                      onClick={() => handleLanguageSelect(language.code)}
                      aria-selected={selectedLanguage === language.code}
                    >
                      <span className="option-flag">
                        {language.nativeName.includes('à¤¹à¤¿à¤¨à¥à¤¦à¥€') ? 'ðŸ‡®ðŸ‡³' : 
                         language.nativeName.includes('à°¤à±†à°²à±à°—à±') ? 'ðŸ‡®ðŸ‡³' : 
                         language.nativeName.includes('à®¤à®®à®¿à®´à¯') ? 'ðŸ‡®ðŸ‡³' : 'ðŸŒ'}
                      </span>
                      <span className="option-text">
                        <span className="native-name">{language.nativeName}</span>
                        <span className="english-name">{language.name}</span>
                      </span>
                      {selectedLanguage === language.code && (
                        <span className="checkmark">âœ“</span>
                      )}
                    </button>
                  ))}
                </div>
              </div>
            )}
          </div>
        </div>
      </header>

      {/* Chat Messages */}
      <div className="chat-messages">
        {messages.length === 0 ? (
          <div className="empty-chat">
            <div className="empty-icon">ðŸ’¬</div>
            <h3>Start Your Health Consultation</h3>
            <p>Describe your symptoms or ask any health-related questions</p>
            <div className="empty-features">
              <div className="feature">
                <span className="feature-icon">ðŸŽ¤</span>
                <span>Voice input supported</span>
              </div>
              <div className="feature">
                <span className="feature-icon">ðŸŒ</span>
                <span>Multiple languages</span>
              </div>
              <div className="feature">
                <span className="feature-icon">âš•ï¸</span>
                <span>Professional medical advice</span>
              </div>
            </div>
          </div>
        ) : (
          messages.map((message) => (
            <div
              key={message.id}
              className={`message ${message.sender === 'user' ? 'user-message' : 'bot-message'}`}
            >
              <div className="message-content">
                <div className="message-avatar">
                  {message.sender === 'user' ? 'ðŸ‘¤' : 'âš•ï¸'}
                </div>
                <div className="message-bubble">
                  <div className="message-text">
                    {formatMessageWithLineBreaks(renderSafeContent(message.text))}
                  </div>
                  <span className="message-time">
                    {new Date(message.timestamp).toLocaleTimeString()}
                  </span>
                  
                  {/* Speaker button for bot messages */}
                  {message.sender === 'bot' && (
                    <button
                      className={`speaker-btn ${currentlySpeakingId === message.id ? 'speaking' : ''}`}
                      onClick={() => {
                        if (currentlySpeakingId === message.id && isSpeaking) {
                          stopSpeaking();
                        } else {
                          speakMessage(message.text, message.id);
                        }
                      }}
                      title={currentlySpeakingId === message.id ? 'Stop reading' : 'Read aloud'}
                      aria-label={currentlySpeakingId === message.id ? 'Stop reading' : 'Read aloud'}
                      disabled={isSpeaking && currentlySpeakingId !== message.id}
                    >
                      {currentlySpeakingId === message.id && isSpeaking ? 'ðŸ”‡' : 'ðŸ”Š'}
                    </button>
                  )}
                </div>
              </div>
            </div>
          ))
        )}
        
        {isTyping && (
          <div className="message bot-message typing-message">
            <div className="message-content">
              <div className="message-avatar">âš•ï¸</div>
              <div className="message-bubble">
                <div className="typing-indicator">
                  <span>Dr. CareBot is typing</span>
                  <div className="typing-dots">
                    <span></span>
                    <span></span>
                    <span></span>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}
        <div ref={messagesEndRef} />
      </div>

      {/* Input Area */}
      <div className="chat-input-container">
        <form className="chat-input-form" onSubmit={handleSendMessage}>
          <div className="input-wrapper">
            <div className="input-field">
              <input
                type="text"
                value={inputMessage}
                onChange={(e) => setInputMessage(e.target.value)}
                placeholder={`Describe your symptoms or ask a question... (${getLanguageName(selectedLanguage)})`}
                disabled={isTyping}
                className="message-input"
                aria-label="Message input"
              />
              
              <div className="input-actions">
                {browserSupportsSpeechRecognition && (
                  <button
                    type="button"
                    className={`action-btn mic-btn ${isListening ? 'listening' : ''}`}
                    onClick={handleMicClick}
                    disabled={isTyping}
                    title={isListening ? 'Stop listening' : 'Start voice input'}
                    aria-label={isListening ? 'Stop listening' : 'Start voice input'}
                  >
                    <span className="btn-icon">{isListening ? 'â¹ï¸' : 'ðŸŽ¤'}</span>
                    <span className="btn-tooltip">
                      {isListening ? 'Stop Listening' : 'Voice Input'}
                    </span>
                  </button>
                )}
                
                {/* Stop speech button */}
                <button
                  type="button"
                  className={`action-btn speaker-btn ${isSpeaking ? 'speaking' : ''}`}
                  onClick={stopSpeaking}
                  disabled={!isSpeaking}
                  title={isSpeaking ? 'Stop all speech' : 'No speech active'}
                  aria-label={isSpeaking ? 'Stop all speech' : 'No speech active'}
                >
                  <span className="btn-icon">ðŸ”‡</span>
                  <span className="btn-tooltip">
                    {isSpeaking ? 'Stop All Speech' : 'No Speech'}
                  </span>
                </button>
              </div>
            </div>
            
            <button 
              type="submit" 
              disabled={isTyping || inputMessage.trim() === ''}
              className="send-button"
              aria-label="Send message"
            >
              <span className="send-icon">âž¤</span>
              <span className="send-text">Send</span>
            </button>
          </div>
        </form>

        {isListening && (
          <div className="speech-indicator">
            <div className="pulse-ring"></div>
            <div className="speech-text">
              <span className="listening-text">Listening in {getLanguageName(selectedLanguage)}...</span>
              <span className="transcript">{transcript}</span>
            </div>
            <button 
              className="stop-listening-btn"
              onClick={stopListening}
              title="Stop listening"
              aria-label="Stop listening"
            >
              â¹ï¸
            </button>
          </div>
        )}
      </div>

      {/* Stop Speech Button - Fixed Position */}
      {isSpeaking && (
        <div className="stop-speech-container">
          <button 
            className="stop-speech-btn"
            onClick={stopSpeaking}
            title="Stop all speech"
            aria-label="Stop all speech"
          >
            <span className="stop-icon">â¹ï¸</span>
            <span className="stop-text">Stop All Voice</span>
          </button>
        </div>
      )}

      {/* Debug Information - Remove in production */}
      {process.env.NODE_ENV === 'development' && (
        <div style={{
          position: 'fixed',
          bottom: '10px',
          left: '10px',
          background: 'rgba(0,0,0,0.8)',
          color: 'white',
          padding: '10px',
          fontSize: '12px',
          zIndex: 10000,
          maxWidth: '300px',
          borderRadius: '5px'
        }}>
          <div><strong>Debug Info:</strong></div>
          <div>Speech Supported: {speechSynthesizer.isSupported() ? 'Yes' : 'No'}</div>
          <div>Speech Initialized: {speechSynthesizer.isInitialized ? 'Yes' : 'No'}</div>
          <div>Currently Speaking: {isSpeaking ? 'Yes' : 'No'}</div>
          <div>Selected Language: {selectedLanguage}</div>
          <div>Available Voices: {speechSynthesizer.getAvailableVoices().length}</div>
        </div>
      )}
    </div>
  );
};

export default ChatInterface;
