import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Dashboard.css";

const actionData = [
  {
    icon: "üí¨",
    title: "Start Chat",
    desc: "Chat with our health assistant about your symptoms or concerns",
    path: "/chat",
  },
  {
    icon: "üë§",
    title: "My Profile",
    desc: "View and update your personal information and medical history",
    path: "/profile",
  },
  {
    icon: "üìã",
    title: "Chat History",
    desc: "Review your previous conversations with the health assistant",
    path: "/history",
  },
];

const tips = [
  "Stay hydrated by drinking at least 8 glasses of water daily",
  "Get 7-9 hours of quality sleep each night",
  "Include physical activity in your daily routine",
  "Eat a balanced diet with plenty of fruits and vegetables",
];

const Dashboard = ({ user, onLogout }) => {
  const navigate = useNavigate();
  const [clicked, setClicked] = useState(false);

  const handleNavigation = (path) => {
    if (clicked) return; // prevent double click
    setClicked(true);
    const container = document.querySelector(".dashboard-container");
    container.classList.add("fade-scale-click");
    setTimeout(() => {
      navigate(path);
    }, 600); // Timing matches animation duration
  };

  return (
    <div className="dashboard-container">
      <header className="dashboard-header glass-float">
        <h1 className="animated-gradient">
          <span className="logo-icon">üçÉ</span> Health Assistant
        </h1>
        <div className="user-menu floating-glow">
          <span>Welcome, {user?.name}</span>
          <button onClick={onLogout} className="logout-btn glowing-hover fixed-size-btn">
            Logout
          </button>
        </div>
      </header>

      <main className="dashboard-main">
        <section className="welcome-section">
          <h2>How can we help you today?</h2>
          <p>Get personalized health advice and information from our AI assistant.</p>
        </section>

        <section className="action-cards">
          {actionData.map(({ icon, title, desc, path }, i) => (
            <div
              key={title}
              role="button"
              tabIndex={0}
              onClick={() => handleNavigation(path)}
              onKeyPress={(e) => e.key === "Enter" && handleNavigation(path)}
              className="action-card fade-up lift-hover clickable fixed-size-card reduced-shadow"
              style={{ animationDelay: `${0.1 + i * 0.15}s` }}
            >
              <div className="card-icon shimmering">{icon}</div>
              <h3 className="animated-glow-text">{title}</h3>
              <p>{desc}</p>
            </div>
          ))}
        </section>

        <section className="quick-tips glass-float fade-up" style={{ animationDelay: "0.6s" }}>
          <div className="tips-gradient"></div>
          <h3>Health Tips</h3>
          <ul>
            {tips.map((tip, idx) => (
              <li key={idx} className="fade-in" style={{ animationDelay: `${0.7 + idx * 0.09}s` }}>
                {tip}
              </li>
            ))}
          </ul>
        </section>
      </main>
    </div>
  );
};

export default Dashboard;
