import React, { useState, useRef, useEffect } from 'react';
import io from 'socket.io-client';
import './ChatInterface.css';

const ChatInterface = ({ user }) => {
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const [connectionStatus, setConnectionStatus] = useState('disconnected');
  const messagesEndRef = useRef(null);
  const socketRef = useRef();

  useEffect(() => {
    // Connect to Socket.IO server
    socketRef.current = io('http://localhost:5000', {
      auth: {
        token: localStorage.getItem('token')
      }
    });

    socketRef.current.on('connect', () => {
      console.log('Connected to server');
      setConnectionStatus('connected');
    });

    socketRef.current.on('disconnect', () => {
      console.log('Disconnected from server');
      setConnectionStatus('disconnected');
    });

    socketRef.current.on('chat_message', (data) => {
      setIsTyping(false);
      
      // Ensure we're only storing strings, not objects
      const newMessage = {
        id: Date.now(),
        text: typeof data.message === 'string' ? data.message : JSON.stringify(data.message),
        sender: 'bot',
        timestamp: new Date(),
        structuredData: data.structuredData
      };
      
      setMessages(prev => [...prev, newMessage]);
    });

    socketRef.current.on('bot_typing', () => {
      setIsTyping(true);
    });

    socketRef.current.on('error', (error) => {
      console.error('Socket error:', error);
      setIsTyping(false);
      setMessages(prev => [...prev, {
        id: Date.now(),
        text: "Sorry, I'm having trouble connecting. Please try again.",
        sender: 'bot',
        timestamp: new Date()
      }]);
    });

    // Load chat history
    fetchChatHistory();

    return () => {
      if (socketRef.current) {
        socketRef.current.disconnect();
      }
    };
  }, []);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const fetchChatHistory = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/chat/history', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      
      if (response.ok) {
        const history = await response.json();
        
        // Ensure all messages have string content
        const formattedHistory = history.map(chat => ({
          id: chat.chat_id || Date.now(),
          text: typeof chat.query === 'string' ? chat.query : JSON.stringify(chat.query),
          sender: 'user',
          timestamp: new Date(chat.timestamp),
          response: typeof chat.response === 'string' ? chat.response : JSON.stringify(chat.response)
        }));
        
        setMessages(formattedHistory);
      }
    } catch (error) {
      console.error('Error fetching chat history:', error);
    }
  };

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  const handleSendMessage = (e) => {
    e.preventDefault();
    if (inputMessage.trim() === '') return;

    const newMessage = {
      id: Date.now(),
      text: inputMessage,
      sender: 'user',
      timestamp: new Date()
    };

    setMessages(prev => [...prev, newMessage]);
    setInputMessage('');
    setIsTyping(true);

    // Send message to server via Socket.IO
    if (socketRef.current) {
      socketRef.current.emit('user_message', {
        message: inputMessage,
        userId: user.id
      });
    }
  };

  const renderStructuredData = (data) => {
    if (!data || typeof data !== 'object') return null;
    
    return (
      <div className="structured-response">
        {data.symptoms && data.symptoms !== "Not available" && (
          <div className="response-section">
            <strong>Symptoms:</strong> {String(data.symptoms)}
          </div>
        )}
        {data.precautions && data.precautions !== "Please consult a doctor" && (
          <div className="response-section">
            <strong>Precautions:</strong> {String(data.precautions)}
          </div>
        )}
        {data.medicine && data.medicine !== "Not available" && (
          <div className="response-section">
            <strong>Medicine:</strong> {String(data.medicine)}
          </div>
        )}
        {data.food && data.food !== "Maintain a balanced diet" && (
          <div className="response-section">
            <strong>Food:</strong> {String(data.food)}
          </div>
        )}
        {data.other && data.other !== "Service temporarily unavailable" && (
          <div className="response-section">
            <strong>Other:</strong> {String(data.other)}
          </div>
        )}
      </div>
    );
  };

  // Safe message rendering function
  const renderMessageContent = (message) => {
    if (typeof message === 'string') {
      return message;
    }
    
    if (typeof message === 'object' && message !== null) {
      // If it's an object, try to stringify it safely
      try {
        return JSON.stringify(message);
      } catch (e) {
        return String(message);
      }
    }
    
    return String(message);
  };

  return (
    <div className="chat-container">
      <header className="chat-header">
        <h2>Health Assistant</h2>
        <div className="user-info">
          <span>Hello, {user?.name}</span>
          <span className={`connection-status ${connectionStatus}`}>
            {connectionStatus === 'connected' ? 'ðŸŸ¢ Connected' : 'ðŸ”´ Disconnected'}
          </span>
        </div>
      </header>

      <div className="chat-messages">
        {messages.length === 0 ? (
          <div className="empty-chat">
            <p>Start a conversation with your health assistant!</p>
            <p>You can ask about symptoms, medications, or general health advice.</p>
          </div>
        ) : (
          messages.map((message) => (
            <div
              key={message.id}
              className={`message ${message.sender === 'user' ? 'user-message' : 'bot-message'}`}
            >
              <div className="message-content">
                <p>{renderMessageContent(message.text)}</p>
                {message.structuredData && renderStructuredData(message.structuredData)}
                {message.response && (
                  <div className="response-content">
                    <p>{renderMessageContent(message.response)}</p>
                  </div>
                )}
                <span className="message-time">
                  {new Date(message.timestamp).toLocaleTimeString()}
                </span>
              </div>
            </div>
          ))
        )}
        {isTyping && (
          <div className="message bot-message">
            <div className="message-content">
              <div className="typing-indicator">
                <span></span>
                <span></span>
                <span></span>
              </div>
            </div>
          </div>
        )}
        <div ref={messagesEndRef} />
      </div>

      <form className="chat-input-form" onSubmit={handleSendMessage}>
        <input
          type="text"
          value={inputMessage}
          onChange={(e) => setInputMessage(e.target.value)}
          placeholder="Type your health question here..."
          disabled={isTyping}
        />
        <button type="submit" disabled={isTyping || inputMessage.trim() === ''}>
          Send
        </button>
      </form>
    </div>
  );
};

export default ChatInterface;
